const bytecode = "";

// Connect to the network
let provider = ethers.getDefaultProvider('ropsten');

// Load the wallet to deploy the contract with -- from metamask account
let privateKey = '0x0123456789012345678901234567890123456789012345678901234567890123';
let wallet = new ethers.Wallet(privateKey, provider);

// Deployment is asynchronous, so we use an async IIFE
(async function () {

    // Create an instance of a Contract Factory
    let factory = new ethers.ContractFactory(abi, bytecode, wallet);

    // Notice we pass in "Hello World" as the parameter to the constructor
    //let contract = await factory.deploy("Hello World");
    let contract = await factory.deploy();

    // The address the Contract WILL have once mined
    // See: https://ropsten.etherscan.io/address/0x2bd9aaa2953f988153c8629926d22a6a5f69b14e
    console.log("Contract Address=" + contract.address);
    // "0x2bD9aAa2953F988153c8629926D22A6a5F69b14E"

    // The transaction that was sent to the network to deploy the Contract
    // See: https://ropsten.etherscan.io/tx/0x159b76843662a15bd67e482dcfbee55e8e44efad26c5a614245e12a00d4b1a51
    console.log("Contract deploy hash=" + contract.deployTransaction.hash);
    // "0x159b76843662a15bd67e482dcfbee55e8e44efad26c5a614245e12a00d4b1a51"

    // The contract is NOT deployed yet; we must wait until it is mined
    await contract.deployed();

    // Done! The contract is deployed.

    const fact = 'The Time 03/Jan/2009 Chancellor on brink of second bailout for banks.';
    await addFact(privateKey, abi, contract.address, fact);

    const factResult = await getFact(0,contract.address,abi);
    console.log("factResult=" + factResult);

    const factCount = await getFactsCount(contract.address,abi);
    console.log("factCount=" + factCount);

})();

async function addFact(privateKey,abi,contractAddress,fact) {
    let wallet = new ethers.Wallet(privateKey,provider);
    let contract = new ethers.Contract(contractAddress,abi,wallet);

    const tx = await contract.add(fact);
    await tx.wait();
    return tx;
}

async function getFact(index, contractAddress, abi) {
    const contract = new ethers.Contract(contractAddress, abi, provider);
    return await contract.getFact(index);

}

async function getFactsCount(contractAddress,abi) {
    const contract = new ethers.Contract(contractAddress, abi, provider);
    return await contract.count()
}